local speedEnabled = false
local originalWalkSpeed = LocalPlayer.Character and LocalPlayer.Character.Humanoid.WalkSpeed or 16

SpeedToggleButton.MouseButton1Click:Connect(function()
    speedEnabled = not speedEnabled
    if speedEnabled then
        SpeedToggleButton.BackgroundColor3 = Color3.fromRGB(0, 150, 0)
        SpeedToggleButton.Text = "✔"
        local newSpeed = tonumber(SpeedTextBox.Text)
        if newSpeed then
            if LocalPlayer.Character and LocalPlayer.Character.Humanoid then
                LocalPlayer.Character.Humanoid.WalkSpeed = newSpeed
            end
        end
    else
        SpeedToggleButton.BackgroundColor3 = Color3.fromRGB(150, 0, 0)
        SpeedToggleButton.Text = "✖"
        if LocalPlayer.Character and LocalPlayer.Character.Humanoid then
            LocalPlayer.Character.Humanoid.WalkSpeed = originalWalkSpeed
        end
    end
end)

SpeedTextBox.FocusLost:Connect(function(enterPressed)
    if enterPressed then
        local newSpeed = tonumber(SpeedTextBox.Text)
        if newSpeed and speedEnabled then
            if LocalPlayer.Character and LocalPlayer.Character.Humanoid then
                LocalPlayer.Character.Humanoid.WalkSpeed = newSpeed
            end
        end
    end
end)

