-- Function to create a toggle button with icon (for Seeds, Gear, Pets)
local function createToggleItem(parentFrame, itemName, scriptCode)
    local ItemFrame = Instance.new("Frame")
    ItemFrame.Name = itemName .. "Frame"
    ItemFrame.Size = UDim2.new(1, 0, 0.08, 0)
    ItemFrame.BackgroundTransparency = 1
    ItemFrame.Parent = parentFrame

    local ItemLabel = Instance.new("TextLabel")
    ItemLabel.Name = "ItemLabel"
    ItemLabel.Size = UDim2.new(0.7, 0, 1, 0)
    ItemLabel.Position = UDim2.new(0, 0, 0, 0)
    ItemLabel.BackgroundTransparency = 1
    ItemLabel.Text = itemName
    ItemLabel.TextColor3 = Color3.fromRGB(255, 255, 255)
    ItemLabel.Font = Enum.Font.Gotham
    ItemLabel.TextSize = 16
    ItemLabel.TextXAlignment = Enum.TextXAlignment.Left
    ItemLabel.Parent = ItemFrame

    local ToggleButton = Instance.new("TextButton")
    ToggleButton.Name = "ToggleButton"
    ToggleButton.Size = UDim2.new(0.1, 0, 0.8, 0)
    ToggleButton.Position = UDim2.new(0.8, 0, 0.1, 0)
    ToggleButton.BackgroundColor3 = Color3.fromRGB(150, 0, 0) -- Red for inactive
    ToggleButton.Text = "✖"
    ToggleButton.TextColor3 = Color3.fromRGB(255, 255, 255)
    ToggleButton.Font = Enum.Font.GothamBold
    ToggleButton.TextSize = 20
    ToggleButton.Parent = ItemFrame

    local active = false
    local connection = nil

    ToggleButton.MouseButton1Click:Connect(function()
        active = not active
        if active then
            ToggleButton.BackgroundColor3 = Color3.fromRGB(0, 150, 0)
            ToggleButton.Text = "✔"
            -- Execute the script code in a loop
            connection = game:GetService("RunService").Heartbeat:Connect(function()
                local success, err = pcall(function()
                    loadstring(scriptCode)()
                end)
                if not success then
                    warn("Error executing script for " .. itemName .. ": " .. err)
                end
            end)
        else
            ToggleButton.BackgroundColor3 = Color3.fromRGB(150, 0, 0)
            ToggleButton.Text = "✖"
            if connection then
                connection:Disconnect()
                connection = nil
            end
        end
    end)
end)

