-- Add all seeds to the Seeds content frame
for _, seedName in ipairs(seedsList) do
    local scriptCode = string.format([[
        while true do
            local args = {[1] = "%s"}
            local success, err = pcall(function()
                game:GetService("ReplicatedStorage").GameEvents.BuySeedStock:FireServer(unpack(args))
            end)
            if not success then
                warn("Failed to buy %s: " .. tostring(err))
            end
            wait(0.1) -- Small delay to prevent spam
        end
    ]], seedName, seedName)
    
    createToggleItem(SeedContentFrame, seedName, scriptCode)
end

